import { useState, useEffect } from 'react';

function App() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5000/api/moodle-courses');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json();
        setCourses(Array.isArray(result) ? result : []);
        setError(null);
      } catch (error) {
        setError(error.message);
        setCourses([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (loading) return <div className="p-6 text-center text-lg">Loading courses...</div>;
  if (error) return <div className="p-6 text-center text-red-600">Error: {error}</div>;
  if (courses.length === 0) return <div className="p-6 text-center">No courses found.</div>;

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Moodle Courses</h1>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {courses.map((course) => (
          <div key={course.id} className="border rounded-lg p-4 bg-white shadow hover:shadow-lg transition">
            <h2 className="text-xl font-semibold mb-2">{course.fullname}</h2>
            <p className="text-gray-600 text-sm mb-3">{course.shortname}</p>

            <div className="text-sm text-gray-700 space-y-1">
              {course.categoryid && <p><strong>Category ID:</strong> {course.categoryid}</p>}
              {course.idnumber && <p><strong>ID Number:</strong> {course.idnumber}</p>}
              {course.startdate && <p><strong>Start Date:</strong> {new Date(course.startdate * 1000).toLocaleDateString()}</p>}
            </div>

            <button className="mt-4 w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-3 rounded transition">
              View Course
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
